package com.aebiz.b2b2c.servicestaff.servicestaffentry.dao;import java.util.List;import org.hibernate.Query;import org.springframework.stereotype.Repository;import com.aebiz.b2b2c.baseframework.basecrud.dao.BaseH4Impl;import com.aebiz.b2b2c.servicestaff.servicestaffentry.vo.ServicestaffentryModel;import com.aebiz.b2b2c.servicestaff.servicestaffentry.vo.ServicestaffentryQueryModel;@Repositorypublic class ServicestaffentryH4Impl extends BaseH4Impl<ServicestaffentryModel,ServicestaffentryQueryModel> implements ServicestaffentryDAO {		/**	 * 根据家政员编号号获取入职信息	 */	public ServicestaffentryModel getServicestaffentryModelByServicestaffUuid(			String servicestaffUuid) {		StringBuffer hql = new StringBuffer(				" from ServicestaffentryModel where serviceStaffUuid=:servicestaffUuid");		Query query = this.getH4Session().createQuery(hql.toString());		query.setString("servicestaffUuid", servicestaffUuid);		List list =query.list();				if(list.size()>0){			return (ServicestaffentryModel) list.get(0);		}				return null;	}}