package com.aebiz.b2b2c.servicestaff.servicestaffinterview.dao;import java.util.List;import org.hibernate.Query;import org.springframework.stereotype.Repository;import com.aebiz.b2b2c.baseframework.basecrud.dao.BaseH4Impl;import com.aebiz.b2b2c.servicestaff.servicestaffinterview.vo.ServicestaffinterviewModel;import com.aebiz.b2b2c.servicestaff.servicestaffinterview.vo.ServicestaffinterviewQueryModel;@Repositorypublic class ServicestaffinterviewH4Impl extends BaseH4Impl<ServicestaffinterviewModel,ServicestaffinterviewQueryModel> implements ServicestaffinterviewDAO {	/**	 * 根据家政员编号号获取面试信息	 */	public ServicestaffinterviewModel getServicestaffinterviewModelByServicestaffUuid(			String servicestaffUuid) {		StringBuffer hql = new StringBuffer(				" from ServicestaffinterviewModel where serviceStaffUuid=:servicestaffUuid");		Query query = this.getH4Session().createQuery(hql.toString());		query.setString("servicestaffUuid", servicestaffUuid);		List list =query.list();				if(list.size()>0){			return (ServicestaffinterviewModel) list.get(0);		}				return null;	}}