package com.aebiz.b2b2c.servicestaff.servicestaffinterview.service.impl;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.aebiz.b2b2c.baseframework.basecrud.service.BaseServiceImpl;import com.aebiz.b2b2c.baseframework.basecrud.vo.BaseModel;import com.aebiz.b2b2c.baseframework.utils.DateFormatHelper;import com.aebiz.b2b2c.baseframework.utils.LoginUserHelper;import com.aebiz.b2b2c.baseframework.uuidservice.service.UuidService;import com.aebiz.b2b2c.servicestaff.servicestaff.service.ServicestaffService;import com.aebiz.b2b2c.servicestaff.servicestaff.vo.ServicestaffModel;import com.aebiz.b2b2c.servicestaff.servicestaffentry.service.ServicestaffentryService;import com.aebiz.b2b2c.servicestaff.servicestaffentry.vo.ServicestaffentryModel;import com.aebiz.b2b2c.servicestaff.servicestaffinterview.dao.ServicestaffinterviewDAO;import com.aebiz.b2b2c.servicestaff.servicestaffinterview.service.ServicestaffinterviewService;import com.aebiz.b2b2c.servicestaff.servicestaffinterview.vo.ServicestaffinterviewModel;import com.aebiz.b2b2c.servicestaff.servicestaffinterview.vo.ServicestaffinterviewQueryModel;@Service@Transactionalpublic class ServicestaffinterviewServiceImpl extends BaseServiceImpl<ServicestaffinterviewModel,ServicestaffinterviewQueryModel> implements ServicestaffinterviewService {	private ServicestaffinterviewDAO myDao = null;	@Autowired	private UuidService us;	@Autowired	public void setMyDao(ServicestaffinterviewDAO dao){		this.myDao = dao;		super.setDao(dao);	}	@Override	public String create(ServicestaffinterviewModel m) {		m.setUuid(us.getNextUuid("Servicestaffinterview",10));		m.setDelFlag(BaseModel.DEL_FLAG_VALID);		m.setOper(LoginUserHelper.getLoginUserUuid());		m.setOpeTime(DateFormatHelper.getNowTimeStr());				String ret = super.create(m);		return ret;	}	@Override	public void update(ServicestaffinterviewModel m) {		m.setDelFlag(BaseModel.DEL_FLAG_VALID);		m.setOper(LoginUserHelper.getLoginUserUuid());		m.setOpeTime(DateFormatHelper.getNowTimeStr());		super.update(m);		}	@Override	public void delete(ServicestaffinterviewModel m) {		m.setDelFlag(BaseModel.DEL_FLAG_INVALID);		m.setOper(LoginUserHelper.getLoginUserUuid());		m.setOpeTime(DateFormatHelper.getNowTimeStr());		super.update(m);	}			/* 家政员信息信息service */	@Autowired	private ServicestaffService servicestaffService;		/* 面试信息表service */	@Autowired	private ServicestaffentryService servicestaffentryService;		/**	 * 根据家政员编号号获取面试信息	 */	public ServicestaffinterviewModel getServicestaffinterviewModelByServicestaffUuid(			String servicestaffUuid) {				return myDao.getServicestaffinterviewModelByServicestaffUuid(servicestaffUuid);	}			/**	 *  根据传入的uuid改变冻结状态	 * 	 * @param customerModel	 * @return 	 */	public Boolean changefrozenSate(String uuid) {		ServicestaffModel s = servicestaffService.getByUuid(uuid);		s.setFrozenSate("4");		servicestaffService.update(s);		return true;	}	/**	 *创建一个家政员入职表，通过家政员编号于之关联	 * 注：此方法是当管理员审核面试的家政员通过后，转入职时用到的一个方法，目的是转入职时时创建一张通过家政员编号关联的入职表	 * @param servicestaffModel	 */	public void createServicestaffentry(String uuid) {		ServicestaffentryModel servicestaffentryModel = new ServicestaffentryModel();		servicestaffentryModel.setServiceStaffUuid(uuid);				servicestaffentryService.create(servicestaffentryModel);	}		}