package com.aebiz.b2b2c.servicestaff.servicestafflevel.vo;import javax.persistence.Entity;import javax.persistence.Table;import javax.persistence.Transient;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;import com.aebiz.b2b2c.baseframework.basecrud.vo.BaseModel;import com.aebiz.b2b2c.baseframework.filemgr.service.FileService;import com.aebiz.b2b2c.baseframework.filemgr.vo.FileModel;import com.aebiz.b2b2c.baseframework.utils.StringUtil;/** *  * @author hedongfei * */@Entity@Table(name="service_staff_level")@Componentpublic class ServicestafflevelModel extends BaseModel{	/*注解文件的service*/	@Transient	private static FileService fileService;		@Autowired	public void setFileService(FileService fileService) {		this.fileService = fileService;	}		/*等级名称*/	private String levelName;		/*等级图标*/	private String levelIcon;		/*等级排序*/	private String position;		/*等级备注*/	private String description;			/*图片上传路径*/	@Transient	private String imgUrl = "";		public String getImgUrl() {		if (StringUtil.isEmpty(this.levelIcon)) {			return "";		}		FileModel file = fileService.getOneFileModel(this.levelIcon);		if (file != null) {			return file.getRemotePaths();		} else {			return this.getLevelIcon();		}	}			public void setImgUrl(String imgUrl) {		this.imgUrl = imgUrl;	}	public void setLevelName(String obj){		this.levelName = obj;	}	public String getLevelName(){		return this.levelName;	}		public void setLevelIcon(String obj){		this.levelIcon = obj;	}	public String getLevelIcon(){		return this.levelIcon;	}		public void setDescription(String obj){		this.description = obj;	}	public String getPosition() {		return position;	}	public void setPosition(String position) {		this.position = position;	}	public String getDescription(){		return this.description;	}				public String toString(){		return super.toString()+" , Model"+this.getClass().getName()+"[levelName=" + this.getLevelName() + ",levelIcon=" + this.getLevelIcon() + ",POSITION=" + this.getPosition() + ",description=" + this.getDescription() + ",]";	}	}